<?phpnamespace App\Repository;use App\Entity\CitationInternaute;use App\Entity\Tag;use App\Service\UtilityTools;use Doctrine\Bundle\DoctrineBundle\Repository\ServiceEntityRepository;use Doctrine\Common\Persistence\ManagerRegistry;/** * @method CitationInternaute|null find($id, $lockMode = null, $lockVersion = null) * @method CitationInternaute|null findOneBy(array $criteria, array $orderBy = null) * @method CitationInternaute[]    findAll() * @method CitationInternaute[]    findBy(array $criteria, array $orderBy = null, $limit = null, $offset = null) */class CitationInternauteRepository extends ServiceEntityRepository{    public function __construct(ManagerRegistry $registry)    {        parent::__construct($registry, CitationInternaute::class);    }    /**     * @param $start     * @param $offset     * @param $order     * @return array     */    public function findAllCitationInternaute($start, $offset, $order): array    {        $order = explode(" ", $order);        return $this->createQueryBuilder('c')            ->orderBy($order[0] , $order[1])            ->setFirstResult($start)            ->setMaxResults($offset)            ->getQuery()            ->getResult();    }}